//imrc import react components snippet
import React, { Component } from "react";

//cc class component snippet
class Counter extends Component {
  //state objects include any data in this counter class
  state = {
    count: 0,
    tags: ["tag1", "tag2", "tag3"]
  };
  styles = {
    fontSize: 10,
    fontWeight: "bold"
  };
  //rendertags is a method to create list iteratively if tags array has values and outputs text if array is empty
  renderTags() {
    if (this.state.tags.length === 0) return <p>No data</p>;
    return (
      <ul>
        {this.state.tags.map(tag => (
          <li key={tag}>{tag}</li>
        ))}
      </ul>
    );
  }
  //render method
  render() {
    return (
      //created react.fragment instead of <div> so we don't have extra div due to index.html
      <React.Fragment>
        <button className="btn btn-secondary btn-sm">Increment</button>
        {this.renderTags()}
      </React.Fragment>
    );
  }
  //adding
  getBadgeClasses() {
    let classes = "badge m-2 badge-";
    classes += this.state.count === 0 ? "warning" : "primary";
    return classes;
  }
  //adding formatCount Method
  formatCount() {
    //destructure the object and calling the count property so don't have to keep typing this.state.count
    const { count } = this.state;
    return count === 0 ? "Zero " : count; //if count = 0 then return "zero else return count value"
  }
}

export default Counter;

