//component to call multiple counter components
import React, { Component } from "react";
import Counter from "./counter";

class Counters extends Component {
  state = {
    counters: [{ id: 1, value: 4 }, { id: 2, value: 0 }, { id: 3, value: 0 }]
  };
  handleIncrement = counter => {
    console.log(counter);
    counter += 1;
    return counter;
  };
  handleReset = () => {
    //call a new array and reset to zero using arrow function and map function, then return
    const counters = this.state.counters.map(c => {
      c.value = 0;
      return c;
    });
    console.log("Handler Reset", counters);
    this.setState({ counters });
  };
  handleDelete = counterId => {
    console.log("Handler Delete", counterId);
    //list comprehension to use filter method that only shows items that was not clicked.
    const counters = this.state.counters.filter(c => c.id !== counterId);
    this.setState({ counters });
  };
  //button.btn.btn-primary.btn-small.m-2
  render() {
    return (
      <div>
        <button
          onClick={this.handleReset}
          className="btn btn-primary btn-small m-2"
        >
          Reset
        </button>
        {this.state.counters.map(counter => (
          //passing event handler to onDelete from Counter to modify state values in this component
          <Counter
            key={counter.id}
            //event property to delete calling method handleDelete
            onDelete={this.handleDelete}
            onIncrement={this.handleIncrement}
            counter={counter} //passing all the data about the counter object instead of calling individual attributes.
            //value={counter.value}
            //id={counter.id}
          />
        ))}
      </div>
    );
  }
}

export default Counters;
